$(".brick-item").mouseenter(function(){
	$(this).css({"box-shadow":" darkgrey 10px 10px 30px 10px" });
	$(this).children(".review-wrapper").addClass("brick-item-active");
})
$(".brick-item").mouseleave(function(){
	$(this).css({"box-shadow":"none" });
	$(this).children(".review-wrapper").removeClass("brick-item-active");

})